
public class BreakDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<10;i++)
		{
			if(i==5)
				continue;

			System.out.println(i);
		
		
		}
	}
		//break: terminates the operation
		//continue: skips the current iteration

}
