
public class ForEach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a[]= {10,20,30,40,50};
		for(int temp:a)
		{
			System.out.println(temp);
		}
		
		String s[]= {"hello","welcome","to","c2tc"};
		for(String temp:s)
		{
			System.out.println(temp);
		}

		
}
}